package com.amigoscode.devicemanagement.external.storage.device;

import com.amazonaws.services.dynamodbv2.datamodeling.DynamoDBAttribute;
import com.amazonaws.services.dynamodbv2.datamodeling.DynamoDBAutoGeneratedKey;
import com.amazonaws.services.dynamodbv2.datamodeling.DynamoDBHashKey;
import com.amazonaws.services.dynamodbv2.datamodeling.DynamoDBTable;
import com.amazonaws.services.dynamodbv2.datamodeling.DynamoDBTypeConverted;
import com.amigoscode.devicemanagement.external.storage.utils.ZonedDateTimeConverter;
import lombok.*;


import java.time.ZonedDateTime;

@DynamoDBTable(tableName = "Devices")
@Builder
@AllArgsConstructor
@NoArgsConstructor
@Getter
@Setter
public class DeviceEntity {

    @DynamoDBHashKey
    @DynamoDBAutoGeneratedKey
    private String id;

    @DynamoDBAttribute
    private String name;

    @DynamoDBAttribute
    private String ownerId;

    @DynamoDBTypeConverted(converter = ZonedDateTimeConverter.class)
    @DynamoDBAttribute
    private ZonedDateTime createdAt;

    @DynamoDBTypeConverted(converter = ZonedDateTimeConverter.class)
    @DynamoDBAttribute
    private ZonedDateTime updatedAt;

    @DynamoDBTypeConverted(converter = ZonedDateTimeConverter.class)
    @DynamoDBAttribute
    private ZonedDateTime deletedAt;

    @DynamoDBAttribute
    private String updatedBy;


    public DeviceEntity(String name, String ownerId) {
        this.name = name;
        this.ownerId = ownerId;
    }

    @Override
    public boolean equals(Object o) {
        if (this == o) return true;
        if (o == null || getClass() != o.getClass()) return false;
        DeviceEntity that = (DeviceEntity) o;
        return id.equals(that.id);
    }
    @Override
    public int hashCode() {
        return 0;
    }
}
